class Component {
    constructor(props){
        this.props = props
    }
    setState=(newState)=>{ // object or callback function
        console.log('state', this.state)// this refers to instance of this class but not in prototype function just normal function
        this.state = newState// it will be instance method, not prototype method
        console.log(this.state)
    }
}


const MyReact ={
    Component, 
}

let obj = {
    a: 1,
    b: 2
}
//Immutable way
obj ={
    ...obj,
    a: 2
}




console.log(obj)

export default MyReact
