import React from 'react';
import ReactDOM from 'react-dom';
import MyReactDOM from './MyReactDOM/MyReactDOM';
import './index.css';

class MyTitle extends React.Component {
  constructor(props){
     super(props) //help you to invoke the constructor of React.Component

  }

  render() {
    return (
    <section 
        className='my-section'
        onClick={()=>{
          alert('hello')
        }} 
        id="my" style={{color: 'red'}} onClick={() => (alert('Hello'))}>
      <header>{this.props.title}</header>
      <h1>
       <span>Hello</span>
      </h1>
      <button>Add</button>
    </section>
    )
  }
}

class MyApp extends React.Component {
  constructor(props){
     super(props) //help you to invoke the constructor of React.Component

  }

  render() {
    return (
    <section 
        className='my-section'
        onClick={()=>{
          alert('hello')
        }} 
        id="my" style={{color: 'red'}} onClick={() => (alert('Hello'))}>
      <header>{this.props.title}</header>
      <h1>
       <span>Hello</span>
      </h1>
      <button>Add</button>
    </section>
    )
  }
}
//MyApp is a class (function) | <MyApp /> is an object ---> convert to js, react.createElement, return detailedHtml
//react element and real element
//<MyApp /> is React.createElement(Myapp, {})


MyReactDOM.render(<MyApp title="MyApp" />, document.getElementById('root'))
