class Component {
    constructor(props){
        this.props = props
    }
    setState=(newState)=>{ // object or callback function
        console.log('state', this.state)// this refers to instance of this class but not in prototype function just normal function
        console.log("Component this:", this)// it will be instance method, not prototype method
    }
}


const MyReact ={
    Component, 
}

export default MyReact
